/*
 *By listing the first six prime numbers: 2, 3, 5, 7, 11, and 13, we can see that the 6th prime is 13.
 *What is the 10001st prime number?
 */

#include<iostream>
#include<string>
#include<sstream>
#define MAX 1000000
#define LIM 10001
using namespace std;



int mul(char a, char b, char c, char d, char e)
{
int x[5]={a-'1'+1,b-'1'+1,c-'1'+1,d-'1'+1,e-'1'+1};
return x[0]*x[1]*x[2]*x[3]*x[4];
}

int main()
{
int product=0;
string str="";
//string str="123456789";

for(int i=0;i<str.length()-5;i++)
{
int new_product= mul(str[i], str[i+1], str[i+2], str[i+3], str[i+4]);
	if(product<new_product)
	product=new_product;
}
cout<<"result:"<<product;

return 0;
}
